import React, { Component } from "react";
import './App.css';
import * as THREE from "three";

class App extends Component {

  componentDidMount() {

    var scene = new THREE.Scene();

    var camera = new THREE.PerspectiveCamera(
      50,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );

    var renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);

    this.mount.appendChild(renderer.domElement);

    var geometry = new THREE.BoxGeometry(1, 1, 1);
    var material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    var cube = new THREE.Mesh(geometry, material);
    scene.add(cube);

    camera.position.z = 50;

    var geometry1 = new THREE.Geometry();
        geometry1.vertices.push(new THREE.Vector3( -1, 0, 0) );
        geometry1.vertices.push(new THREE.Vector3( 0, 1, 0) );
        geometry1.vertices.push(new THREE.Vector3( 1, 0, 0) );
        geometry1.vertices.push(new THREE.Vector3( 0, -1, 0) );
        geometry1.vertices.push(new THREE.Vector3( -1, 0, 0) );

    var material1 = new THREE.LineBasicMaterial( { color: 0x0000ff } );

    var line = new THREE.Line( geometry1, material1 );

    scene.add(line);

    var geometry2 = new THREE.CircleGeometry( 10, 50 );
    var material2 = new THREE.MeshBasicMaterial( { color: 0x42ff00, wireframe: true } );
    var circle = new THREE.Mesh( geometry2, material2 );
    scene.add( circle );

    var animate = function() {
      requestAnimationFrame(animate);

      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;

      renderer.render(scene, camera);
    };

    animate();
  }

  render() {
    return <div ref={ref => (this.mount = ref)} />;
  }
}

export default App;
