import React, { Component } from "react";
import './App.css';
import * as THREE from "three";

class App extends Component {

  componentDidMount() {

    var scene = new THREE.Scene();

    var camera = new THREE.PerspectiveCamera(
      50,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );

    var renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);

    this.mount.appendChild(renderer.domElement);

    camera.position.z = 50;

    var geometry3, material3, circle;


    for (var i = 2; i < 22; i++) {
          geometry3 = new THREE.RingBufferGeometry((i-0.1), i, 100);
          material3 = new THREE.MeshBasicMaterial( { color: 0x42ff00 } );
          circle = new THREE.Mesh( geometry3, material3 );
          //scene.add(circle);
          i = i + 2;
    }

    var geometry4 = new THREE.RingBufferGeometry(15, 15, 100);
    var material4 = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
    var point = new THREE.Mesh( geometry4, material4 );
    scene.add(point);

    var animate = function() {
      requestAnimationFrame(animate);

      point.rotation.x += 0.01;
      point.rotation.y += 0.01;

      renderer.render(scene, camera);
    };

    animate();
  }

  render() {
    return <div ref={ref => (this.mount = ref)} />;
  }
}

export default App;
